mapper.select:
select 
		t.dutyid, t.deptid, t.createdate, t.createby, t.id
	from aos_dutydatares t 

	where 1 = 1  
	 and 
		t.dutyid = #{dutyid}

	 and 
		t.deptid = #{deptid}

	 and 
		t.createdate = #{createdate}

	 and 
		t.createby = #{createby}

	 and 
		t.id = #{id}

	order by t.createdate desc

select 
		t.dutyid, t.deptid, t.createdate, t.createby, t.id
	from aos_dutydatares t 

	where 1 = 1  
	 and 
		t.dutyid = #{dutyid}

	 and 
		t.deptid = #{deptid}

	 and 
		t.createdate = #{createdate}

	 and 
		t.createby = #{createby}

	 and 
		t.id = #{id}

mapper.insert:
insert into aos_dutydatares
	(id 		 , dutyid
 , deptid
 , createdate
 , createby

)
values 			(#{id} 		 , #{dutyid}
 , #{deptid}
 , #{createdate}
 , #{createby}

)

mapper.update:
update aos_dutydatares 
	set id   = #{id}
		 , dutyid = #{dutyid}
		 , deptid = #{deptid}
		 , createdate = #{createdate}
		 , createby = #{createby}

where id = #{id}
